mapper.insert:
INSERT INTO novel (
    novel_id, title, intro, user_id, genre, created_at, creation_id
) VALUES (
    novel_seq.NEXTVAL, #{title}, #{intro}, #{userId}, #{genre}, #{createdAt}, #{creationId}
)

mapper.update:
